# dg(x)/dx関数 ややこしいのでこのxをVaRでかく
dg.dx <- function(VaR, theta, par){
  f <- function(x, theta, par){
    exp(theta*x)*dfas2(x, mu=par[1], sigma=par[2],
                       lambda=par[3], delta=par[4])
  }
  #M <- integrate(f, -Inf, Inf, theta=theta, par=par)$value
  M <- integrate(f, -30, 30, theta=theta, par=par)$value
  
  # return( integrate(m.f, -Inf, Inf, theta=theta, par=par)$value )
  return( exp(theta*VaR)*(theta*f(VaR,theta,par) + df.dx(VaR,mu=par[1], sigma=par[2],
                                                         lambda=par[3], delta=par[4])) /M)
}

find.location.theta <- function(p, par){
  VaR<- qfas(p, mu=par[1], sigma=par[2], lambda=par[3], delta=par[4])
  f <- function(x) return( dg.dx(VaR, x, par) )
  out <- uniroot(f, interval=c(-10,10),extendInt="yes", trace=1)$root
out}


### 指数変換した重点分布の密度関数(ロケーションシフト)  
d.location.IS <- function(x, theta,par){
  # 重点分布の密度関数の分子
  f <- function(x, theta, par){
    exp(theta*x)*dfas2(s_inverse((x-par[1])/par[2],par[3]), mu=par[1], sigma=par[2],
                       lambda=par[3], delta=par[4])
  }
  # 分母
  M <- integrate(f, -30, 30, theta=theta, par=par)$value
  return( f(x, theta, par)/M )
}



# 重点分布(d.location.IS)からのサンプリング
rIS_location_SIR <- function(n, par, par2, theta){
  ## 正規分布を提案分布に
  q <- rnorm(n, mean=par2, sd=15)
  ## 重み
  w <- sapply(q, 
              d.location.IS, theta =theta, par=par)/dnorm(q, mean=par2, sd=15)
  w <- w/sum(w)
  ## resample
  q.resample <- Resample1(q, weight=w, NofSample = n)
  list( q=q.resample, w=w)
}
